        $result_html = preg_replace("|([\S])+[\s]+([.,:])|ui","$1$2",$result_html);
        // Добавить пробел после этих знаков препинания (если его нет).
        $result_html = preg_replace("|([.,;])([\S])|ui","$1 $2",$result_html);

